package Jacekpaczka;

public class zadaniajavowe {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 1;
		int y = 1;
		int z = 1;
		
		for (x=1;x<20;x++) {
			System.out.println(x);
		}
		
		
		do {
			System.out.println(y);
			y++;
		} while (y<20);
		
		
		while (z<20) {
			System.out.println(z);
			z++;
		}
	}

}
